package com.jtarun.practice.leetcode;

import java.util.*;

/** 839
 * Two strings X and Y are similar if we can swap two letters (in different positions) of X, so that it equals Y.
 *
 * For example, "tars" and "rats" are similar (swapping at positions 0 and 2), and "rats" and "arts" are similar,
 * but "star" is not similar to "tars", "rats", or "arts".
 *
 * Together, these form two connected groups by similarity: {"tars", "rats", "arts"} and {"star"}.  Notice that "tars"
 * and "arts" are in the same group even though they are not similar.  Formally, each group is such that a word is in
 * the group if and only if it is similar to at least one other word in the group.
 *
 * We are given a list A of strings.  Every string in A is an anagram of every other string in A.  How many groups are
 * there?
 */
public class SimilarStringGroups {

    private static class Solution {

        private static class UnionFind {
            int[] parents;
            int count;

            UnionFind(int n) {
                parents = new int[n];
                for (int i = 0; i < n; i++) parents[i] = i;
                count = n;
            }

            void union(int i, int j) {
                int x = find(i);
                int y = find(j);
                if (x != y) {
                    parents[x] = y;
                    count--;
                }
            }

            int find(int i) {
                List<Integer> path = new ArrayList<>();
                while (i != parents[i]) {
                    path.add(i);
                    i = parents[i];
                }
                for (int x : path) parents[x] = i;
                return i;
            }

        }

        public int numSimilarGroups(String[] A) {

            int n = A.length;
            if (n == 0) return 0;

            UnionFind uf = new UnionFind(n);
            List<int[]> edges = new ArrayList<>();
            for (int i = 1; i < n; i++) {
                for (int j = 0; j < i; j++) {
                    if (similar(A[i], A[j])) {
                        uf.union(i, j);
                    }
                }
            }

            return uf.count;
        }


        public int numSimilarGroups2(String[] A) {
            int n = A.length;
            if (n == 0) return 0;

            List<int[]> edges = new ArrayList<>();
            for (int i = 1; i < n; i++) {
                for (int j = 0; j < i; j++) {
                    if (similar(A[i], A[j])) {
                        edges.add(new int[]{i, j});
                    }
                }
            }

            Map<Integer, List<Integer>> h = new HashMap<>();
            for (int[] edge : edges) {
                h.computeIfAbsent(edge[0], k -> new ArrayList<>()).add(edge[1]);
                h.computeIfAbsent(edge[1], k -> new ArrayList<>()).add(edge[0]);
            }

            int res = 0;
            boolean[] v = new boolean[n];

            for (int i = 0; i < n; i++) {
                if (!v[i]) {
                    bfs(h, i, v);
                    res++;
                }
            }
            return res;
        }

        private void bfs(Map<Integer, List<Integer>> h, int i, boolean[] v) {
            if (v[i]) return;
            v[i] = true;
            Queue<Integer> q = new LinkedList<>();
            q.offer(i);
            while (!q.isEmpty()) {
                int t = q.poll();

                if (h.get(t) == null) continue;
                for (int child : h.get(t)) {
                    if (!v[child]) {
                        v[child] = true;
                        q.offer(child);
                    }
                }
            }
        }

        private void dfs(Map<Integer, List<Integer>> h, int i, boolean[] v) {
            if (v[i]) return;
            v[i] = true;
            if (h.get(i) == null) return;

            for (int child : h.get(i)) {
                if (!v[child]) {
                    dfs(h, child, v);
                }
            }
        }

        private boolean similar(String s, String t) {
            int diff = 0;
            for (int i = 0; i < s.length(); i++) {
                if (s.charAt(i) != t.charAt(i)) diff++;
            }
            return (diff <= 2);
        }
    }

    public static void main(String[] args) {
        Solution sol = new Solution();
        String[] strings1 = {"kccomwcgcs","socgcmcwkc","sgckwcmcoc","coswcmcgkc","cowkccmsgc","cosgmccwkc","sgmkwcccoc",
                "coswmccgkc","kowcccmsgc","kgcomwcccs"};

        String[] strings = {
                "","","","","","","","","","","","","","","","","","","",""
        };

        //int res = sol.numSimilarGroups(new String[]{"tars","rats","arts","star"});
        int res = sol.numSimilarGroups(strings);
        System.out.println(res);
    }

}
